con:configuration.path:
declare namespace SOAP-ENV='http://schemas.xmlsoap.org/soap/envelope/';
declare namespace s='http://www.w3.org/2001/XMLSchema';
declare namespace ns1='http://tempuri.org';

//*:Home/*:Street

con:configuration.scriptText:
import com.eviware.soapui.support.XmlHolder


def holder = new XmlHolder( messageExchange.responseContentAsXml )
def responseXml = holder.getPrettyXml()
def homeZip = holder.getNodeValue('//*:Home/*:Zip')
def officeZip =holder.getNodeValue('//*:Office/*:Zip')
assert homeZip!=officeZip

con:configuration.path:
declare namespace SOAP-ENV='http://schemas.xmlsoap.org/soap/envelope/';
declare namespace s='http://www.w3.org/2001/XMLSchema';
declare namespace ns1='http://tempuri.org';
<list>
{
for $x in //*:FindPersonResult/*
where $x //*:State[contains(text(), "NY")]
return
<zip>
{$x/*:Zip/text()}</zip>
}
</list>

con:configuration.scriptText:
import com.eviware.soapui.support.XmlHolder
def holder = new XmlHolder( messageExchange.responseContentAsXml )
def responseXml = holder.getPrettyXml()
def street = context.expand('${Properties#Street}')
log.info(street)
def city = context.expand('${Properties#City}')
def zip = context.expand('${Properties#Zip}')
def state = context.expand('${Properties#State}')

def street2 = context.expand('${Properties#Street2}')
def city2 = context.expand('${Properties#City2}')
def zip2 = context.expand('${Properties#Zip2}')
def state2 = context.expand('${Properties#State2}')

def homeStr = holder.getNodeValue('//*:Home/*:Street')
def homeCt = holder.getNodeValue('//*:Home/*:City')
def homeSt = holder.getNodeValue('//*:Home/*:State')
def homeZip = holder.getNodeValue('//*:Home/*:Zip')
def officeStr =holder.getNodeValue('//*:Office/*:Street')
def officeCt =holder.getNodeValue('//*:Office/*:City')
def officeSt =holder.getNodeValue('//*:Office/*:State')
def officeZip =holder.getNodeValue('//*:Office/*:Zip')
assert street==homeStr
assert city==homeCt
assert state==homeSt
assert zip==homeZip
assert street2==officeStr
assert city2==officeCt
assert state2==officeSt
assert zip2==officeZip
